import React, { useState } from 'react';
import { useUser } from './'; // Import useUser
import { useNavigate } from 'react-router-dom';

const ProfileEdit = () => {
    const { user, setUser } = useUser(); // Access and update user data
    const [formData, setFormData] = useState({
        bannerImage: null,
        name: user.name,
        email: user.email,
        description: '',
        languages: user.language,
        education: user.education,
        specialization: user.specialization,
        socialMedia: user.socialMedia
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleFileChange = (e) => {
        setFormData(prevState => ({
            ...prevState,
            bannerImage: e.target.files[0]
        }));
    };

    const handleSocialMediaChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            socialMedia: {
                ...prevState.socialMedia,
                [name]: value
            }
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Update user data in context
        setUser(prevState => ({
            ...prevState,
            ...formData,
            bannerImage: formData.bannerImage || prevState.bannerImage
        }));

        // Navigate to Profile View page
        navigate('/profile');
    };

    return (
        <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
            <h2>Edit Profile</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="bannerImage">Banner Image:</label>
                    <input
                        type="file"
                        id="bannerImage"
                        name="bannerImage"
                        onChange={handleFileChange}
                        style={{ width: '100%', padding: '8px' }}
                    />
                </div>
                {/* Other form fields similar to before */}
                <button type="submit" style={{ padding: '10px 20px', background: '#3498db', color: '#fff', border: 'none', borderRadius: '5px' }}>
                    Save Changes
                </button>
            </form>
        </div>
    );
};

export default ProfileEdit;
