import React from 'react';
import { Link } from 'react-router-dom'; 
import back from "../images/BACK.webp";
import '../components/ProfileView.css';
import { FaUser, FaEdit } from 'react-icons/fa';
import { useUser } from '' // Import useUser

const ProfileView = () => {
    const { user } = useUser(); // Access user data from context

    return (
        <div style={{ position: "relative", height: "810px", width: "100%" }}>
            <img className='img' src={user.bannerImage || back} alt="Banner" />
            <h1 className="text">Content Creator Profile</h1>
            <div className="details">
                <p><strong>Name:</strong> {user.name}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Language:</strong> {user.language}</p>
                <p><strong>Education:</strong> {user.education}</p>
                <p><strong>Specialization:</strong> {user.specialization}</p>
                <div className="social-media">
                    <a href={user.socialMedia.twitter} target="_blank" rel="noopener noreferrer">
                        <i className="fab fa-twitter"></i>
                    </a>
                    <a href={user.socialMedia.linkedin} target="_blank" rel="noopener noreferrer">
                        <i className="fab fa-linkedin-in"></i>
                    </a>
                    <a href={user.socialMedia.github} target="_blank" rel="noopener noreferrer">
                        <i className="fab fa-github"></i>
                    </a>
                </div>
            </div>
            <div className='profile-actions'>
                <Link to="/profile/edit" className="profile-edit">
                    <FaEdit /> {/* Edit icon */}
                </Link>
                <Link to="/profile" className="profile-icon">
                    <FaUser /> {/* Profile icon */}
                </Link>
            </div>
        </div>
    );
};

export default ProfileView;
