import React, { useState } from 'react';

const ProfileEdit = () => {
    const [formData, setFormData] = useState({
        bannerImage: null, // Change to null to hold the file
        name: '',
        email: '',
        description: '',
        languages: '',
        education: '',
        specialization: '',
        socialMedia: {
            twitter: '',
            linkedin: '',
            github: ''
        }
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleFileChange = (e) => {
        setFormData({
            ...formData,
            bannerImage: e.target.files[0] // Store the selected file
        });
    };

    const handleSocialMediaChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            socialMedia: {
                ...formData.socialMedia,
                [name]: value
            }
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Create a FormData object to handle file upload
        const data = new FormData();
        data.append('bannerImage', formData.bannerImage);
        data.append('name', formData.name);
        data.append('email', formData.email);
        data.append('description', formData.description);
        data.append('languages', formData.languages);
        data.append('education', formData.education);
        data.append('specialization', formData.specialization);
        data.append('socialMedia[twitter]', formData.socialMedia.twitter);
        data.append('socialMedia[linkedin]', formData.socialMedia.linkedin);
        data.append('socialMedia[github]', formData.socialMedia.github);

        // Handle form submission, e.g., sending data to the server
        console.log('Form Data Submitted:', formData);

        // Here you can add your logic to send `data` to the server
    };

    return (
        <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
            <h2>Edit Profile</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="bannerImage">Banner Image:</label>
                    <input
                        type="file"
                        id="bannerImage"
                        name="bannerImage"
                        onChange={handleFileChange}
                        style={{ width: '100%', padding: '8px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="name">Name:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        placeholder="Enter your name"
                        style={{ width: '100%', padding: '8px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        placeholder="Enter your email"
                        style={{ width: '100%', padding: '8px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="description">Description:</label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        placeholder="Enter a brief description about yourself"
                        style={{ width: '100%', padding: '8px', minHeight: '100px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="languages">Languages (comma-separated):</label>
                    <input
                        type="text"
                        id="languages"
                        name="languages"
                        value={formData.languages}
                        onChange={handleChange}
                        placeholder="Enter languages"
                        style={{ width: '100%', padding: '8px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="education">Education:</label>
                    <input
                        type="text"
                        id="education"
                        name="education"
                        value={formData.education}
                        onChange={handleChange}
                        placeholder="Enter your education"
                        style={{ width: '100%', padding: '8px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="specialization">Specialization:</label>
                    <input
                        type="text"
                        id="specialization"
                        name="specialization"
                        value={formData.specialization}
                        onChange={handleChange}
                        placeholder="Enter your specialization"
                        style={{ width: '20', padding: '8px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Social Media Links:</label>
                    <div style={{ marginBottom: '10px' }}>
                        <label htmlFor="twitter">Twitter:</label>
                        <input
                            type="text"
                            id="twitter"
                            name="twitter"
                            value={formData.socialMedia.twitter}
                            onChange={handleSocialMediaChange}
                            placeholder="Enter your Twitter URL"
                            style={{ width: '200px', padding: '8px' }}
                        />
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                        <label htmlFor="linkedin">LinkedIn:</label>
                        <input
                            type="text"
                            id="linkedin"
                            name="linkedin"
                            value={formData.socialMedia.linkedin}
                            onChange={handleSocialMediaChange}
                            placeholder="Enter your LinkedIn URL"
                            style={{ width: '200px', padding: '8px' }}
                        />
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                        <label htmlFor="github">GitHub:</label>
                        <input
                            type="text"
                            id="github"
                            name="github"
                            value={formData.socialMedia.github}
                            onChange={handleSocialMediaChange}
                            placeholder="Enter your GitHub URL"
                            style={{ width: '200px', padding: '8px' }}
                        />
                    </div>
                </div>

                <button type="submit" style={{ padding: '10px 20px', background: '#3498db', color: '#fff', border: 'none', borderRadius: '5px' }}>
                    Save Changes
                </button>
            </form>
        </div>
    );
};

export default ProfileEdit;
